('5dd0435cb26d2ac', 'browseedit', 'sample', 'Sample Upload Form', NULL, '1', NULL, 'sample', 'sample_id', NULL, NULL, NULL, '0', NULL, 'SELECT * FROM sample', '// ** Configuration: Modify if necessary\n// *****************************************\nvar uploadFolder = \'libs/upload/documents/\';\nvar idFileName = \'files_file_name\';\nvar idFileId = \'files_file_id\';\nvar idSubForm = \'sample_files\';\n\n// *****************************************\n\nvar idRowFileName;\nvar idRowFileId;\n\n\nfunction uploadFile(event) {\n    var td = $(event.target).parent();\n    var t = td.attr(\'data-nu-prefix\');\n\n    idRowFileName = t + idFileName;\n    idRowFileId = t + idFileId;\n\n    $(\"#fileToUpload\").click();\n\n}\n\nfunction createDownloadLink(field, folder, fileId, fileName) {\n\n    $(\'#\' + field)\n    .css({\"text-decoration\": \"underline\"})\n    .css(\'cursor\', \'pointer\')\n    .off(\'click\')\n    .attr({\n        fileName: fileName,\n        fileId: fileId,\n        folder: folder\n    })\n    .attr(\'readonly\', \'readonly\')\n    .click(function(event) {\n        downloadFile($(this).attr(\'folder\') + $(this).attr(\'fileid\') + \'_\' + $(this).attr(\'fileName\'), $(this).attr(\'fileName\'));\n    });\n}\n\n\nfunction addDownloadLinks(subform) {\n\n    const sf = nuSubformObject(subform);\n    const cName = sf.fields.indexOf(idFileName);\n    const cId = sf.fields.indexOf(idFileId);\n\n    for (let i = 0; i < sf.rows.length; i++) {\n\n        const fileId = sf.rows[i][cId];\n        const fileName = sf.rows[i][cName];\n\n        if (fileName !== \'\') {\n            createDownloadLink(idSubForm + nuPad3(i) + idFileName, uploadFolder, fileId, fileName);\n        }\n    }\n}\n\n\nfunction downloadFile(url, filename) {\n    var a = document.createElement(\"a\");\n    a.href = url;\n    a.setAttribute(\"download\", filename);\n    document.body.appendChild(a);\n    a.click();\n    document.body.removeChild(a);\n}\n\nif (nuFormType() == \'edit\') {\n    addDownloadLinks(idSubForm);\n} else {\n\n    if (typeof nuStopBrowserResize == \'function\') {\n        nuStopBrowserResize();\n    }\n\n}', NULL, NULL, NULL, NULL, NULL),
