('nu5fee06b1acbe4bb', 'NUBACKUP', 'Create a Database Backup using mysqldump', 'nubuilder', '// Use the correct namespace\nuse Ifsnop\\Mysqldump\\Mysqldump;\n\n// Get the JSON settings passed from JS via the \'params\' property\n$params = nuGetProperty(\'NUBACKUP_params\');\n$settings = $params ? nuDecode($params)[\'settings\'] : [];\n\n// Call backup process using the decoded settings\nnuBackupRun($settings);\n\nfunction nuBackupRun($settings) {\n\n	nuBackupLoadMysqldumpLibrary(\'../third_party/mysqldump/Mysqldump.php\');\n\n	// Extract values with defaults using null coalescing operator\n	$directory = $settings[\'directory\'] ?? \'\';\n	$fileName = $settings[\'fileName\'] ?? \'\';\n	$includeTables = $settings[\'include-tables\'] ?? [];\n	$excludeTables = $settings[\'exclude-tables\'] ?? [];\n	$includeViews = $settings[\'include-views\'] ?? [];\n	$nuRecordsFilter = $settings[\'nuRecordsFilter\'] ?? \'\'; // nu, user, empty = all (default)\n	unset($settings[\'nuRecordsFilter\']);\n	\n	$nuTableFilter = $settings[\'nuTableFilter\'] ?? \'\'; // nu, user, empty = all (default)\n	unset($settings[\'nuTableFilter\']);\n\n	// Merge default dump settings with any settings passed from JS\n	$dumpSettings = nuBackupBuildDumpSettings($settings);\n\n	// Prepare the backup directory and filename\n	$backupDirectory = nuBackupDirectory($directory);\n	$backupFileName = nuBackupFileName($fileName, $dumpSettings);\n\n	// Set which tables to include/exclude\n	nuBackupSetTables($dumpSettings, $includeTables, $excludeTables, $includeViews, $nuTableFilter);\n\n	$dumper = nuBackupCreateDumper($dumpSettings);\n\n	$tableWheres = nuBackupBuildTableWheres($nuRecordsFilter);\n	$dumper->setTableWheres($tableWheres);\n\n    $backupDirectory = str_replace([\'/\', \'\\\\\'], DIRECTORY_SEPARATOR, $backupDirectory);\n	$dumpFile = $backupDirectory . nuSanitizeFilename($backupFileName);\n	nuBackupCreateDirectoryIfNotExists($backupDirectory);\n\n	nuBackupStartDump($dumper, $dumpFile);\n\n	nuBackupCompleteNotification($backupDirectory, $backupFileName);\n}\n\nfunction nuBackupFileExtension($dumpSettings) {\n	if (!array_key_exists(\'compress\', $dumpSettings) || $dumpSettings[\'compress\'] === \'None\' || $dumpSettings[\'compress\'] === \'\') {\n		return \'\';\n	}\n	return \'.\' . strtolower($dumpSettings[\'compress\']);\n}\n\nfunction nuBackupDirectory($directory) {\n    // Use provided directory or default\n    $backupDir = !empty($directory) ? $directory : \'../backup/\';\n\n    // Ensure the directory exists\n    if (!is_dir($backupDir)) {\n        mkdir($backupDir, 0755, true);\n    }\n\n    // Define the path to index.htm\n    $indexFile = rtrim($backupDir, \'/\\\\\') . \'/index.htm\';\n\n    // If index.htm doesn\'t exist, create it with a hidden comment\n    if (!file_exists($indexFile)) {\n        $comment = \"<!-- for security reasons, do not delete this file. -->\";\n        file_put_contents($indexFile, $comment);\n    }\n\n    return $backupDir;\n}\n\nfunction nuBackupFileName($fileName, $dumpSettings) {\n	// If fileName is provided, use it; otherwise generate a default filename\n	$extension = nuBackupFileExtension($dumpSettings);\n\n	return !empty($fileName)\n		? $fileName . $extension\n		: date(\'Y-m-d_H:i:s\') . \'_\' . uniqid() . \'_nuBuilder_backup.sql\' . $extension;\n\n}\n\nfunction nuBackupBuildDumpSettings($settings = []) {\n\n	$defaults = [\n		\'single-transaction\' => false,\n		\'no-create-info\' => false,\n		\'lock-tables\' => false,\n		\'add-locks\' => false,\n		\'extended-insert\' => false,\n		\'skip-definer\' => true,\n		\'routines\' => true,\n        \'events\' => true,\n		\'compress\' => $settings[\'compress\'] ?? \'None\' // Mysqldump::NONE\n	];\n\n	return array_merge($defaults, $settings);\n\n}\n\nfunction nuBackupLoadMysqldumpLibrary($path) {\n	try {\n		require_once($path);\n	} catch (Exception $e) {\n		nuDisplayError(\'require_once failed! Error: \' . $e->getMessage());\n	}\n}\n\nfunction nuBackupCreateDumper($dumpSettings) {\n	global $nuConfigDBHost,\n	$nuConfigDBName,\n	$nuConfigDBUser,\n	$nuConfigDBPassword,\n	$nuConfigDBPort;\n	$nuConfigDBPort = $nuConfigDBPort ?? \'3306\';\n	$dsn = \"mysql:host={$nuConfigDBHost};dbname={$nuConfigDBName};port={$nuConfigDBPort}\";\n	return new Ifsnop\\Mysqldump\\Mysqldump($dsn, $nuConfigDBUser, $nuConfigDBPassword, $dumpSettings);\n}\n\nfunction nuBackupBuildTableWheres($nuRecordsFilter = \'\') {\n	$tableWheres = [\n		\"zzzzsys_session\" => \"zzzzsys_session_id not like \'s%\'\"\n	];\n\n	if (in_array($nuRecordsFilter, [\'user\', \'nu\'])) {\n		$sysTables = nuGetSysTables();\n		$conditionFunction = ($nuRecordsFilter === \'user\') ? \'nuBackupTableIdNotLikeNu\' : \'nuBackupTableIdLikeNu\';\n		foreach ($sysTables as $tableName) {\n		    if ($tableName !== \'zzzzsys_session\') {\n			    $tableWheres[$tableName] = $conditionFunction($tableName);\n		    }\n		}\n	}\n\n	return $tableWheres;\n}\n\nfunction nuBackupTableIdNotLikeNu($tableName) {\n	return \"{$tableName}_id not like \'nu%\'\";\n}\n\nfunction nuBackupTableIdLikeNu($tableName) {\n	return \"{$tableName}_id like \'nu%\'\";\n}\n\nfunction nuBackupCreateDirectoryIfNotExists($directory) {\n	if (!is_dir($directory)) {\n		mkdir($directory, 0755, true);\n	}\n}\n\nfunction nuBackupStartDump($dumper, $dumpFile) {\n	try {\n		$dumper->start($dumpFile);\n	} catch (Exception $e) {\n		nuDisplayError(\'Export Error: \' . $e->getMessage());\n	}\n}\n\nfunction nuBackupCompleteNotification($backupDirectory, $backupFileName) {\n	$encodedPath = base64_encode($backupDirectory . $backupFileName);\n	$js = \"nuMessage([\'<h2>Export completed!</h2><br>SQL Dump saved in \' + atob(\'$encodedPath\')]);\";\n	nuJavaScriptCallback($js);\n}\n\nfunction nuBackupSetTables(&$dumpSettings, $includeTables, $excludeTables, $includeViews, $nuTableFilter) {\n\n	$dumpSettings[\"include-tables\"] = is_array($includeTables) ? $includeTables : [];\n	$dumpSettings[\"exclude-tables\"] = is_array($excludeTables) ? $excludeTables : [];\n	$dumpSettings[\"include-views\"] = is_array($includeViews) ? $includeViews : [];\n\n    if ($nuTableFilter === \'nu\') {\n        $dumpSettings[\"include-tables\"] = array_merge($dumpSettings[\"include-tables\"], nuGetSysTables());\n        $dumpSettings[\"exclude-tables\"] = array_merge($dumpSettings[\"exclude-tables\"], nuGetUserTables());\n    } else if ($nuTableFilter === \'user\') {\n        $dumpSettings[\"exclude-tables\"] = array_merge($dumpSettings[\"exclude-tables\"], nuGetSysTables());\n        $dumpSettings[\"include-tables\"] = array_merge($dumpSettings[\"exclude-tables\"], nuGetUserTables());\n    }\n\n}\n', 'hide', '', '1', '0', '0', '0', '', NULL),
('nu5ff7efb1ed369a6', 'NUREFRESHSELECTOBJECT', 'Refresh a select object', 'nubuilder', 'function nuGetSelectValues($formId, $selectId) {\n\n    $sql = \"\n        SELECT\n            sob_select_sql\n        FROM\n            `zzzzsys_object`\n        WHERE\n            sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\n    \";\n\n    $t = nuRunQuery($sql, [$formId, $selectId]);\n\n    $a = [];\n    if (db_num_rows($t) == 1) {\n\n        $r = db_fetch_row($t);\n        if ($r != false) {\n            \n            $disS = nuReplaceHashVariables($r[0]);\n\n            $t = nuRunQuery($disS);\n\n            while ($row = db_fetch_row($t)) {\n                $a[] = $row;\n            }\n\n            return json_encode($a);\n        }\n\n    }\n\n    return false;\n\n}\n\nfunction nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix) {\n\n    $j = nuGetSelectValues($formId, $selectId);\n\n    $selectId = $prefix.$selectId;\n\n    $cb = \"if (window.nuSelectObjectRefreshed) {\n    	    nuSelectObjectRefreshed(\'$formId\', \'$selectId\', nuSelectObjectRefreshedCount);\n         }\";\n\n    if ($j == false) {\n        \n        return \"var count = -1; \".$cb;\n        \n    } else {\n    	\n    return \"\n    	function nuPopulateSelectObject() {\n    	\n    		var p = $j;\n    \n    		$(\'#$selectId\').empty();\n    		\n    		if (\'$removeBlank\' == \'0\' ) {\n    		    $(\'#$selectId\').append(\'<option value=\\\"\\\"></option>\');\n    		}\n            \n            var count = 0;\n    \n    		if (p != \'\') {\n    		    var s = nuIsSaved();\n    			\n    			for (var i = 0; i < p.length; i++) {\n    				$(\'#$selectId\').append(\'<option value=\\\"\' + p[i][0] + \'\\\">\' + p[i][1] + \'</option>\');\n    				count ++;\n    			}\n    			\n    			if (s) { nuHasNotBeenEdited(); }\n    			\n    		}\n    		\n    		return count;\n    	}\n    	\n    	var nuSelectObjectRefreshedCount = nuPopulateSelectObject();\n\n    \".$cb;\n    }\n\n}\n\nfunction nuRefreshSelectObject($selectId, $formIdHk, $removeBlank, $prefixHK) {\n\n    if (!nuHasProperty($formIdHk, $formId, false)) {\n        $formId = \'#form_id#\';\n    }\n\n    if (!nuHasProperty($prefixHK, $prefix, false)) {\n        $prefix = \'\';\n    }\n\n    $js = nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix);\n    nuJavaScriptCallback($js);\n\n}\n\nnuRefreshSelectObject(\'#NUREFRESHSELECTOBJECT_selectid#\', \'NUREFRESHSELECTOBJECT_formid\', \'#NUREFRESHSELECTOBJECT_removeblank#\',\'NUREFRESHSELECTOBJECT_prefix\');', 'hide', '', '1', '1', '1', '0', '', NULL),
('nu5ff8249659aef00', 'NUREFRESHDISPLAYOBJECT', 'Refresh a display object', 'nubuilder', 'function nuGetDisplayValue($formId, $id) {\n\n    $sql = \"SELECT sob_display_sql, sob_display_procedure FROM `zzzzsys_object` WHERE sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\";\n    $selectObject = nuRunQuery($sql, [$formId, $id]);\n\n    if (db_num_rows($selectObject) == 1) {\n        $obj = db_fetch_object($selectObject);\n\n        if ($obj != false) {\n\n            $displayProcedure = $obj->sob_display_procedure;\n\n            if (empty($displayProcedure)) {\n\n                $disS = nuReplaceHashVariables($obj->sob_display_sql);\n                $disT = nuRunQuery($disS);\n\n                if (db_num_rows($disT) >= 1) {\n                    $disR = db_fetch_row($disT);\n                    return $disR[0];\n                } else {\n                    return \"\";\n                }\n\n            } else {\n\n                $procedureCode = nuProcedure($displayProcedure);\n                if ($procedureCode !== \'\') {\n                    return nuEval($displayProcedure, $procedureCode);\n                } else {\n                    return false;\n                }\n            }\n        }\n    }\n\n    return false;\n}\n\n\nfunction nuRefreshDisplayObject() {\n\n    $HKPREFIX = \"NUREFRESHDISPLAYOBJECT_\";\n    \n    if (!nuHasProperty($HKPREFIX . \'displayid\', $displayId, false)) {\n        return;\n    }\n\n    if (!nuHasProperty($HKPREFIX . \'formid\', $formId, false)) {\n        $formId = \'#form_id#\';\n    }\n\n    if (!nuHasProperty($HKPREFIX . \'prefix\', $prefix, false)) {\n        $prefix = \'\';\n    }\n\n    $value = nuGetDisplayValue($formId, $displayId);\n    $displayId = $prefix.$displayId;\n\n    if ($value === false && $value !== \'\') {\n        $js = \"nuMessage(nuTranslate(\'Error\'), nuTranslate(\'Failed to refresh the Display Object:\') + \' $displayId\'); \";\n    } else {\n\n        $js = \"\n		    function nuRefreshDisplayObjectSetNewValue(displayId, value) {\n    			let obj = $(\'#\' + displayId);\n    			const format = obj.attr(\'data-nu-format\');\n    			const formattedValue = nuFORM.addFormatting(value, format);\n    			nuSetValue(displayId, formattedValue);\n		    }\n\n		    nuRefreshDisplayObjectSetNewValue(\'$displayId\', \'$value\');\n			if (window.nuDisplayObjectRefreshed) {\n				nuDisplayObjectRefreshed(\'$displayId\', \'$formId\');\n			}\n		\";\n    }\n\n    nuJavaScriptCallback($js);\n\n}\n\nnuRefreshDisplayObject();', 'hide', '', '1', '1', '1', '0', '', NULL),
('nu6068bee1b6c63be', 'NURUNADDDBCOLUMN', 'Add a new database column', 'nubuilder', 'if (nuHash() [\'GLOBAL_ACCESS\'] == \'0\') return;\n\n$sql = \'ALTER TABLE `#sob_all_table#` ADD \' . \'#sql_query#\';\n$after = \'#sql_after_column#\';\nif ($after != \'\') {\n    $sql = $sql . \" AFTER \" . $after;\n}\n\nif (preg_match(\'[DELETE |DROP |INSERT |;]\', strtoupper($sql))) {\n    $r = - 2;\n}\nelse {\n    $r = nuRunQuery($sql, [] , true);\n}\n\nif ($r == 0) {\n    $js = \"nuMessage(nuTranslate(\'Information\'), nuTranslate(\'The column has been created successfully\')); nuRefreshSelectObject(\'sql_after_column\');\";\n}\nelse if ($r == - 1) {\n    $js = \"nuMessage(nuTranslate(\'Error\'), nuTranslate(\'An error occured while creating the column\'), nuTranslate(\'Check nuDebug Results for details\'));\";\n\n}\nelse if ($r == - 2) {\n    $js = \"nuMessage(nuTranslate(\'Error\'), [nuTranslate(\'An error occured while creating the column\'), nuTranslate(\'The query contains invalid keywords\')]);\";\n\n}\n\nnuJavaScriptCallback($js);\n', 'hide', '', '1', '0', '1', '0', '', NULL),
