('nu62c488df22285ea', 'NUDUMPFORMCODES', 'Dump form js and php codes  to files', 'nubuilder', 'if (!nuGlobalAccess()) {\n    return;\n}\n\n// PHP\n$s = \"SELECT sph_code, sph_php FROM `zzzzsys_php`\";\n$t = nuRunQuery($s);\n\nwhile ($r = db_fetch_object($t)) {\n    nuSetupDumpCodeFile(\'php_codes\', $r->sph_code, $r->sph_php, \'\', \"php\");\n}\n\n// Object events\n$s = \"\n    SELECT\n        CONCAT(sfo_code, \'_\', sob_all_id, \'_\', zzzzsys_event_id) as id,\n        sev_javascript\n    FROM\n        zzzzsys_object\n    JOIN zzzzsys_event ON zzzzsys_event.sev_zzzzsys_object_id = zzzzsys_object.zzzzsys_object_id\n    JOIN zzzzsys_tab ON zzzzsys_tab_id = sob_all_zzzzsys_tab_id\n    JOIN zzzzsys_form ON zzzzsys_form_id = syt_zzzzsys_form_id\n    WHERE\n        IFNULL(sev_javascript, \'\') <> \'\'\n\";\n\n$t = nuRunQuery($s);\nwhile ($r = db_fetch_object($t)) {\n    nuSetupDumpCodeFile(\'object_events\', $r->id, $r->sev_javascript, \'\', \"js\");\n}\n\n\n// Form JS\n$s = \"SELECT sfo_code, sfo_javascript, sfo_browse_javascript, sfo_edit_javascript FROM `zzzzsys_form`\";\n$t = nuRunQuery($s);\n\nwhile ($r = db_fetch_object($t)) {\n\n    nuSetupDumpCodeFile(\'js_codes\', $r->sfo_code, $r->sfo_javascript, \'sfo_javascript\', \"js\");\n    nuSetupDumpCodeFile(\'js_codes\', $r->sfo_code, $r->sfo_edit_javascript, \'sfo_edit_javascript\', \"js\");\n    nuSetupDumpCodeFile(\'js_codes\', $r->sfo_code, $r->sfo_browse_javascript, \'sfo_browse_javascript\', \"js\");\n\n}\n\n\nfunction nuSetupDumpCodeFile($folder, $sfoCode, $code, $postfix, $extension) {\n    $postfix = $postfix == \'\' ? \'\' : \"_\" . $postfix;\n    $file = $sfoCode . $postfix . \".\" . $extension;\n    $code = $code == NULL ? \'\' : $code;\n\n    if (strlen($code) > 0) {\n        $dir = dirname(__DIR__, 1) . DIRECTORY_SEPARATOR . \"form_codes\" . DIRECTORY_SEPARATOR . $folder . DIRECTORY_SEPARATOR;\n\n        if (!file_exists($dir)) {\n            mkdir($dir, 0777, true);\n        }\n\n        $file_path = $dir . $file;\n        file_put_contents($file_path, $code);\n    }\n}', 'hide', '', '1', '0', '1', '0', '', NULL),
('nu62da72900a02b9a', 'NUUPLOADFILE_TEMPLATE', 'Upload Files Template', 'nubuilder', '// Allowed file types\n$allowedTypes = [\n    \'image/png\',\n    \'image/jpeg\',\n    \'application/pdf\',\n    \'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet\',\n    \'application/vnd.openxmlformats-officedocument.wordprocessingml.document\',\n    \'application/vnd.openxmlformats-officedocument.presentationml.presentation\',\n    \'text/plain\',\n    \'text/csv\'\n];\n\n// Maximum file size\n$maxFileSize = 5 * 1024 * 1024; // (5 MB)\n\n// Target directory\n$targetDirectory = $_SERVER[\'DOCUMENT_ROOT\'] . \'/\';\n\ntry {\n\n	// Sanitize file name\n	$fileName = nuSanitizeFilename(basename($_FILES[\'file\'][\'name\']));\n\n	// Check file size\n	if ($_FILES[\'file\'][\'size\'] > $maxFileSize) {\n		throw new Exception(\'Exceeded file size limit\');\n	}\n\n	// Check file type\n	$finfo = new finfo(FILEINFO_MIME_TYPE);\n	if (!in_array($finfo->file($_FILES[\'file\'][\'tmp_name\']), $allowedTypes)) {\n		throw new Exception(\'Invalid file type\');\n	}\n\n	// Build target file path\n	$targetFile = $targetDirectory . $fileName;\n\n    if (move_uploaded_file($_FILES[\'file\'][\'tmp_name\'], $targetFile)) {\n        $data = [\'url\' => $targetFile, \'file\' => $fileName, \'message\' => \'The file \' . $fileName . \' has been uploaded.\'];\n        http_response_code(201);\n        $result = json_encode($data);\n    }\n    else {\n        throw new Exception(nuTranslate(\'Unable to move the uploaded file to its final location:\') . $targetFile);\n    }\n\n} catch(\\Throwable $th) {\n\n    $result = nuSetUploadError(\'Sorry, there was an error uploading your file.\');\n\n}', 'hide', '', '0', '1', '1', '1', '', NULL),
