('nu67aa95468188689', 'NUBROWSEDELETEROW', 'Delete a Browse row', 'nubuilder', 'function nuDeleteRowGetFormTableInfo($formId)    {\n\n  $sql = \"SELECT sfo_table, sfo_primary_key FROM `zzzzsys_form` WHERE `zzzzsys_form_id` = ?\";\n  \n  $t        = nuRunQuery($sql, [$formId]);\n  $r        = db_fetch_object($t);\n  \n  return [$r->sfo_table, $r->sfo_primary_key];\n  \n}\n\n\nfunction nuDeleteRowHasDeletePermission($formId)    {\n\n  if(nuGlobalAccess()) { \n      return true;\n  }\n\n  $sql = \"SELECT * FROM zzzzsys_access_form WHERE slf_zzzzsys_access_id = ? AND slf_zzzzsys_form_id = ?\";\n\n  $t        = nuRunQuery($sql, [$groupId, $formId]);\n  $r        = db_fetch_object($t);\n  \n  return $r->slf_delete_button == 1;\n}    \n  \nfunction nuDeleteRowDeleteRecord($formId, $recordId){\n\n  if (nuDeleteRowHasDeletePermission($formId)) {\n      \n      $tableInfo = nuDeleteRowGetFormTableInfo($formId);\n      $tableName = $tableInfo[0];\n      $tablePk = $tableInfo[1];\n\n      $qry = \"DELETE FROM `$tableName` WHERE `$tablePk` = ? \";\n\n      nuRunQuery($qry, [$recordId]);\n      \n      // The function afterDeleteRow() must be declared in the form\'s Custom Code \n      $j = \"nuAfterDeleteRow();\";\n      nuJavascriptCallback($j);\n\n  } else {\n      nuDisplayError(nuTranslate(\"Delete is disabled for this Access Level\"));\n  }\n  \n}\n\nnuDeleteRowDeleteRecord(\"#NUBROWSEDELETEROW_FORM_ID#\", \"#NUBROWSEDELETEROW_RECORD_ID#\");', 'hide', '', '1', '0', '0', '0', '', NULL),
('nu67b5eba0a682823', 'NUFORMATGETDEFAULT', 'Get Default Input Format', 'nubuilder', '$inputType = nuGetProperty(\'NUFORMATGETDEFAULT_INPUTTYPE\');\n$formats = nuGetDefaultFormats();\n$default = $formats[$inputType];\n\n$js  = \" nuSetDefaultFormat(\'$default\'); \";\n\nnuJavaScriptCallback($js);\n\n', 'hide', '', '1', '0', '1', '0', '', NULL),
('nu682ce26ca553942', 'NUGETACCESSLEVELSFROMFORM', 'Copies Access Levels from one form to another', 'nubuilder', '$decodedParams = nuGetProperty(\'params\');\nif ($decodedParams) {\n	$parsedData = nuDecode($decodedParams);\n\n	// Retrieve form IDs\n	$sourceFormID = $parsedData[\'source_form_id\'] ?? \'\';\n	$destinationFormID = $parsedData[\'destination_form_id\'] ?? \'\';\n\n	// Retrieve boolean options (expected as 0 or 1)\n	$deleteAllExisting = isset($parsedData[\'delete_all_existing\']) ? (bool) $parsedData[\'delete_all_existing\'] : false;\n	$skipIfExists = isset($parsedData[\'skip_if_exists\']) ? (bool) $parsedData[\'skip_if_exists\'] : true;\n	$overwriteIfExists = isset($parsedData[\'overwrite_if_exists\']) ? (bool) $parsedData[\'overwrite_if_exists\'] : true;\n\n} \n\n// nuDebug($sourceFormID, $destinationFormID, $deleteAllExisting, $skipIfExists, $overwriteIfExists);\n\n// Validate mutually exclusive options\nif ($skipIfExists && $overwriteIfExists) {\n	$js = \" nuMessage(\\\"Error: \'skipIfExists\' and \'overwriteIfExists\' cannot both be set to true.\\\");\";\n\n	nuJavaScriptCallback($js);  \n	return;\n}\n\n// If deletion is enabled, remove existing rows with the new form ID\nif ($deleteAllExisting) { nuDebug(\'delete existing\');\n	$deleteSQL = \"DELETE FROM zzzzsys_access_form WHERE slf_zzzzsys_form_id = ?\";\n	$deleteResult = nuRunQuery($deleteSQL, [$destinationFormID], true);\n\n	// Check if the DELETE was successful\n	if ($deleteResult === -1) {\n		// Handle the error (optional)\n		$error = nuGetLastError();\n		nuDebug(\'Delete failed:\', $error);\n	}\n}\n\n// Prepare the SELECT query to fetch rows with the old form ID\n$selectSQL = \"\n    SELECT\n        slf_zzzzsys_access_id,\n        slf_add_button,\n        slf_save_button,\n        slf_delete_button,\n        slf_clone_button,\n        slf_new_button,\n        slf_print_button,\n        slf_data_mode,\n        slf_form_type,\n        slf_json\n    FROM\n        zzzzsys_access_form\n    WHERE\n        slf_zzzzsys_form_id = ?\n\";\n\n// Execute the SELECT query\n$stmt = nuRunQuery($selectSQL, [$sourceFormID]);\n\n// Loop through each fetched row\nwhile ($row = db_fetch_object($stmt)) {\n	// Check if a matching row exists in the new form\n	$checkSQL = \"\n        SELECT zzzzsys_access_form_id FROM zzzzsys_access_form\n        WHERE slf_zzzzsys_form_id = ? AND slf_zzzzsys_access_id = ?\n        LIMIT 1\n    \";\n	$checkStmt = nuRunQuery($checkSQL, [$destinationFormID, $row->slf_zzzzsys_access_id]);\n	$existingRow = db_fetch_object($checkStmt);\n\n	if ($existingRow) {\n		if ($skipIfExists) {\n			// Matching row exists, skip insertion\n			continue;\n		} elseif ($overwriteIfExists) {\n			// Delete the existing matching row\n			$deleteMatchSQL = \"\n                DELETE FROM zzzzsys_access_form\n                WHERE zzzzsys_access_form_id = ?\n            \";\n			$deleteMatchResult = nuRunQuery($deleteMatchSQL, [$existingRow->zzzzsys_access_form_id], true);\n\n			// Check if the DELETE was successful\n			if ($deleteMatchResult === -1) {\n				// Handle the error (optional)\n				$error = nuGetLastError();\n				nuDebug(\'Delete matching row failed:\', $error);\n				continue; // Skip insertion if deletion fails\n			}\n		} else {\n			// Matching row exists and neither skip nor overwrite is enabled, skip insertion\n			continue;\n		}\n	}\n\n	// Generate a new unique ID for the primary key\n	$newID = nuID();\n\n	// Prepare the INSERT query to duplicate the row with the new form ID and new primary key\n	$insertSQL = \"\n        INSERT INTO zzzzsys_access_form (\n            zzzzsys_access_form_id,\n            slf_zzzzsys_access_id,\n            slf_zzzzsys_form_id,\n            slf_add_button,\n            slf_save_button,\n            slf_delete_button,\n            slf_clone_button,\n            slf_new_button,\n            slf_print_button,\n            slf_data_mode,\n            slf_form_type,\n            slf_json\n        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)\n    \";\n\n	// Execute the INSERT query with the new values\n	$params = [\n		$newID,\n		$row->slf_zzzzsys_access_id,\n		$destinationFormID,\n		$row->slf_add_button,\n		$row->slf_save_button,\n		$row->slf_delete_button,\n		$row->slf_clone_button,\n		$row->slf_new_button,\n		$row->slf_print_button,\n		$row->slf_data_mode,\n		$row->slf_form_type,\n		$row->slf_json\n	];\n\n	$result = nuRunQuery($insertSQL, $params, true);\n\n	// Check if the INSERT was successful\n	if ($result === -1) {\n		// Handle the error (optional)\n		$error = nuGetLastError();\n		nuDebug(\'Insert failed:\', $error);\n	}\n}\n\n\n$js = \" nuFormImportACLAfterRun();\";\nnuJavaScriptCallback($js); ', 'hide', '', '1', '0', '0', '0', '', NULL),
('nu682ddc25aa0bb5f_BB', 'nu682ddc25aa0bb5f_BB', 'System PHP', 'nubuilder', '$query = \"\n\nSELECT\n    f.*,\n    COUNT(af.slf_zzzzsys_form_id) AS access_levels_count,\n    GROUP_CONCAT(a.sal_code ORDER BY a.sal_code SEPARATOR \', \') AS access_levels\nFROM\n    zzzzsys_form f\nLEFT JOIN zzzzsys_access_form af\n    ON af.slf_zzzzsys_form_id = f.zzzzsys_form_id\nLEFT JOIN zzzzsys_access a\n    ON a.zzzzsys_access_id = af.slf_zzzzsys_access_id\nWHERE\n    f.zzzzsys_form_id NOT LIKE \'nu%\'\n    AND f.sfo_type != \'subform\'\nGROUP BY\n    f.zzzzsys_form_id\nHAVING\n    access_levels_count > 0\nORDER BY\n    f.sfo_code \";\n\n\nnuCreateTableFromSelect(\'#TABLE_ID#\', $query);\n', NULL, NULL, '1', '0', '1', '0', NULL, NULL),
