eColumns();\n    }\n}\n\nvar _nuResizeBrowseColumns = nuResizeBrowseColumns;\nvar nuResizeBrowseColumns = function() {\n    nuResizeBrowseColumnsFix();\n}\n\nfunction nuOnLoad() {\n\n    $(\'#nulink\').html(\'nuBuilder4 sample Database: Uploading Files in a Subform\');\n\n    var f = nuCurrentProperties();\n    var acl = nuAccessLevelCode();\n    var isGlobeAdmin = nuUserName() == null;\n\n    // Show form and record arguments in URL\n    if (window.top === window.self) {\n        if (window.location.search.indexOf(\'?i=\') >= 0)\n            window.history.replaceState(\'\', document.title, \'?f=\' + f.form_id + \'&r=\' + f.record_id);\n        else\n            window.history.pushState(\'\', document.title, \'?f=\' + f.form_id + \'&r=\' + f.record_id);\n    }\n\n    // Add a refresh button for the globeadmin\n    if (isGlobeAdmin == true) {\n        nuAddActionButton(\'custRefreshBtn\', \'Refresh\', \'nuGetBreadcrumb()\');\n    }\n\n    // Transform to Input Type Search\n    if (nuFormType() == \'browse\') {\n        $(\"#nuSearchField\").prop(\"type\", \"search\");\n        $(\'input[type=search]\').on(\'search\', function() {\n            nuSearchAction(1);\n        });\n    }\n\n\n    // Detect when back button clicked: goto previous bookmark\n    window.history.pushState({\n        page: 1\n    }, \"\", \"\");\n    window.onpopstate = function(event) {\n        if (event) {\n            gotoPrevBreadcrumb();\n        } else {}\n    };\n\n    // Change Tab name\n    document.title = \"nuBuilder - \" + window.nuFORM.getProperty(\'title\');\n\n    // Add Menu for Globeadmin\n    if (isGlobeAdmin == true) {\n        var fragment = createFragment(quickMenu);\n        if (window.nuFORM.breadcrumbs.length == 1) {\n            $(fragment).insertAfter(\"#nuOptions\");\n        } else {\n            $(fragment).insertBefore(\"#nuBreadcrumb0\");\n        }\n    }\n\n}\n\nfunction createFragment(htmlStr) {\n    var frag = document.createDocumentFragment(),\n        temp = document.createElement(\'div\');\n    temp.innerHTML = htmlStr;\n    while (temp.firstChild) {\n        frag.appendChild(temp.firstChild);\n    }\n    return frag;\n}\n\nfunction gotoPrevBreadcrumb() {\n\n    if (parent.$(\'#nuModal\').length > 0) {\n        nuClosePopup();\n        return;\n    }\n\n    var l = window.nuFORM.breadcrumbs.length;\n    if (l > 1) {\n        nuGetBreadcrumb(l - 2);\n    }\n}\n	\n</script>\n\n<style>    \n	\n/* Begin:Quickmenu */\n\n.dropbtn {\n    background-color: #00adef; \n    color: #FFF;\n    padding: 0px;\n    padding-left: 5px;\n    margin-right:15px;\n    font-size: 14px;\n    border: none;\n    cursor: pointer;\n}\n\n.dropdown {\n    position: relative;\n    display: inline-block;   \n}\n\n.dropdown-content {\n    display: none;\n    position: absolute;    \n    background-color: #fefefe;\n    min-width: 250px;\n    box-shadow: 0 2px 3px rgba(10,10,10,.1),0 0 0 1px rgba(10,10,10,.1);\n    z-index: 1;\n    white-space: nowrap;\n    border-radius: 4px;\n}\n\n.dropdown-content a {\n    color: black;\n    padding: 5px 16px;\n    text-decoration: none;\n    font-size: 15px;\n    line-height: 1.0;\n    font-family:Helvetica;\n    display: block;\n}\n\n.dropdown-content a:hover {\n    background-color: #3572b0;\n    color: #FFF;\n}\n\n.dropdown:hover .dropdown-content {\n    display: block;\n}\n\n.dropdown-divider {\n    background-color: #dbdbdb;\n    border: none;\n    display: block;\n    height: 1px;\n    margin: .5rem 0\n}\n\n.dropdown:hover .dropbtn {\n    color: #d80147;\n}\n\n\n/* End: Quickmenu */\n	\n\n</style>\n<script>\n', '/* Define your own styles, override styles from nubuilder4.css */\r\n\r\n/*\r\n .nuActionButton {\r\n background-color: #579cb7\r\n}\r\n\r\n*/', '1', NULL);
