('nusession', 'browse', 'nusession', 'Sessions', 'nubuilder', '1', NULL, 'zzzzsys_session', 'zzzzsys_session_id', '', 0, 0, '0', NULL, 'SELECT \n   *\nFROM (\n    SELECT \n        zzzzsys_session_id AS zzzzsys_session_id,\n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.zzzzsys_user_id\')) AS login,\n        NULL AS user,\n        sss_login_time AS login_time,\n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.ip_address\')) AS ip\n    FROM \n        zzzzsys_session\n    WHERE \n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.global_access\')) = \'1\'\n    \n    UNION ALL\n    \n    SELECT \n        zzzzsys_session_id AS zzzzsys_session_id,\n        sus_login_name AS login,\n        sus_name AS user,\n        sss_login_time AS login_time,\n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.ip_address\')) AS ip\n    FROM \n        zzzzsys_user\n        JOIN zzzzsys_session ON zzzzsys_user_id = TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.zzzzsys_user_id\'))\n    WHERE \n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.global_access\')) = \'0\'\n) AS combined_sessions WHERE (1)\nORDER BY \n    login_time DESC\n', 'nuHide(\'nuAddButton\');\n\nfunction nuSessionAddActionButton() {\n    const btn = nuAddActionButton(\'TerminateSession\', \'Terminate Sessions\', \'nuSessionAskTerminateSessions()\');\n    btn.addClass(\'nuSaveButtonEdited\');\n}\n\nfunction nuSessionRunTerminateSessions(option = \'\') {\n    nuSetProperty(\'NUSESSIONTERMINATE_OPTION\', option)\n    nuRunPHPHidden(\"NUSESSIONTERMINATE\");\n}\n\nfunction nuSessionGetScreenWidth() {\n    return window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;\n}\n\nfunction nuSessionGetBoxWidth() {\n    const screenWidth = nuSessionGetScreenWidth();\n    return Math.min(screenWidth * 0.9, 500) + \'px\';\n}\n\nfunction nuSessionAskTerminateSessions() {\n    $.confirm({\n        title: \'